let badMoodTipDay16 = Tip(header: "🤔", title: "Stressed: Re-reframing the stressful event in your mind reduces stress", body: "Change the message you tell yourself from \"this is a stressful situation/task\" to \"this is a challenging but rewarding opportunity to learn.\" \n\nRe-reframing it as learning opportunity shifts your mindset and better prepares you to approach the task.", sourceName: "Harvard Business Review", sourceURL: "https://hbr.org/2018/09/to-cope-with-stress-try-learning-something-new", sponsorLogo: "", sponsorURL: "")
let badMoodTipDay17 = Tip(header: "🤔", title: "Crisis Text Line: Free, confidential, and 24/7 support at your fingertips", body: "Text with a volunteer Crisis Counselor who is trained to bring you from a hot moment to a cool calm. \n\nThe top 3 texter issues are anxiety, depression, and relationships. \n\n• USA: Text HOME to 741741 \n• CAN: Text HOME to 686868 \n• UK: Text HOME to 85258", sourceName: "Business Insider", sourceURL: "https://www.businessinsider.com/important-trait-of-success-2013-11", sponsorLogo: "", sponsorURL: "")
let badMoodTipDay18 = Tip(header: "🤔", title: "Sad: How to reduce sadness", body: "• Cry. \n• Talk to someone. \n• Get out in nature. \n• Listen to upbeat music. \n• Watch a funny TV show. \n• Remember, it will get better. \n• Focus on the good and move on.", sourceName: "Huffington Post", sourceURL: "https://www.huffingtonpost.com/maria-rodale/21-ways-to-deal-with-sadn_b_5803672.html", sponsorLogo: "", sponsorURL: "")
let badMoodTipDay19 = Tip(header: "🤔", title: "Mad: Scientific ways to calm down", body: "• Take deep breaths \n• Take a walk and get some fresh air \n• Write down why you're mad \n• Play loud or heavy music", sourceName: "Bustle", sourceURL: "https://www.bustle.com/articles/134593-6-ways-to-calm-down-when-youre-angry", sponsorLogo: "", sponsorURL: "")

