let badMoodTipDay20 = Tip(header: "🤔", title: "Reduce time online: Teens who spent 5+ hours a day online were twice as likely to be unhappy as those who spent less than 1 hour a day", body: "In one experiment, people who were randomly assigned to give up Facebook for a week ended that time happier, less lonely and less depressed than those who continued to use Facebook.", sourceName: "Quartz", sourceURL: "https://qz.com/1190151/why-am-i-unhappy-a-new-study-explains-americas-unhappiness-epidemic/", sponsorLogo: "", sponsorURL: "")
let badMoodTipDay21 = Tip(header: "", title: "Mad: \"I shall never permit myself to stoop so low as to hate any man.\"", body: "Booker T. Washington \n\nThe most influential spokesman for the African-American community between 1895 and 1915." , sourceName: "Wonderful Quote", sourceURL: "https://www.wonderfulquote.com/a/booker-t-washington-quotes", sponsorLogo: "", sponsorURL: "")
let badMoodTipDay22 = Tip(header: "🤔", title: "Stressed: Viewing stress as a helpful part of life, rather than as harmful, is associated with better health, emotional well-being and productivity", body: "1. View your body's stress response as helpful, not harmful. It's energy you can use; \n\n2. Realize that you can handle, and even grow from, the stress; \n\n3. Everyone deals with stress, and it's not something that proves how uniquely screwed up you or your life is.", sourceName: "Stanford University", sourceURL: "https://news.stanford.edu/pr/2015/pr-stress-embrace-mcgonigal-050715.html", sponsorLogo: "", sponsorURL: "")
let badMoodTipDay23 = Tip(header: "😁", title: "Depressed: People who suffer from depression should participate in yoga and deep (coherent) breathing classes at least twice weekly plus practice at home to receive a significant reduction in their symptoms", body: "Depression is globally responsible for more years lost to disability than any other disease. \n\nThe study supports the use of a yoga and coherent breathing intervention in major depressive disorder in people who are not on antidepressants and in those who have been on a stable dose of antidepressants and have not achieved a resolution of their symptoms." , sourceName: "Reader's Digest", sourceURL: "https://www.rd.com/health/wellness/how-to-be-happy/", sponsorLogo: "", sponsorURL: "")

