let badMoodTipPM12 = Tip(header: "", title: "Studies find that looking at a screen leads to unhappiness", body: "Teens who spent more time on the internet, playing computer games, on social media, texting, using video chat or watching TV were less happy. \n\nTeens who spent more time seeing their friends in person, exercising, playing sports, attending religious services, reading or even doing homework were happier.", sourceName: "Quartz", sourceURL: "https://qz.com/1190151/why-am-i-unhappy-a-new-study-explains-americas-unhappiness-epidemic/", sponsorLogo: "", sponsorURL: "")
let badMoodTipPM13 = Tip(header: "🤔", title: "Depressed: Exercise reduces depression and helps keep depression from coming back once you're feeling better", body: "Exercise releases feel-good endorphins. \n\nFind ways to add small amounts of physical activity throughout your day: \n\n• Take the stairs instead of the elevator. \n• Park a little farther away from work to fit in a short walk.", sourceName: "Mayo Clinic", sourceURL: "https://www.mayoclinic.org/diseases-conditions/depression/in-depth/depression-and-exercise/art-20046495", sponsorLogo: "", sponsorURL: "")
