let screenwriting20 = Tip(header: "⏳", title: "The greatest part of a writer’s time is spent in reading, in order to write; a man will turn over half a library to make one book.", body: "- Samuel Johnson" , sourceName: "SCREENCRAFT", sourceURL: "https://screencraft.org/2013/01/23/50-great-screenwriting-quotes/", sponsorLogo: "", sponsorURL: "")

