//7
let screenwriting24 = Tip(header: "⏳", title: "Write. Rewrite. When not writing or rewriting, read. I know of no shortcuts.", body: "- Larry L. King" , sourceName: "SCREENCRAFT", sourceURL: "https://screencraft.org/2013/01/23/50-great-screenwriting-quotes/", sponsorLogo: "", sponsorURL: "")

