let singleAM5 = Tip(header: "", title: "Dating Coach: Be the type of person you want to attract. Be the best you can be so you attract the best", body: "Too many people are tying to fill voids in themselves by looking for a partner who has the quality they lack. \n\nThis may work on a surface level but it doesn't work on a deeper level.", sourceName: "Bustle", sourceURL: "https://www.bustle.com/articles/181737-9-matchmakers-share-their-best-dating-advice-for-singles", sponsorLogo: "", sponsorURL: "")
let singleAM6 = Tip(header: "", title: "Dating Coach: Take a minute to put some effort into looking your best because you may run into someone you find attractive", body: "Whether its at a work or dentist appointment, the car wash on Sunday or running errands, if you're single you never know who you are going to run into. \n\nLooking your best adds to your sense of confidence and self-esteem and that resonates with everyone around you and draws the [potential partners] in like a magnet.", sourceName: "Bustle", sourceURL: "https://www.bustle.com/articles/181737-9-matchmakers-share-their-best-dating-advice-for-singles", sponsorLogo: "", sponsorURL: "")
let singleAM7 = Tip(header: "", title: "Increase Happiness: Commit to someone compatible with you who shares your interests and values and whose company you enjoy", body: "Research: You're more likely to be happy if you're married or in a committed relationship. \n\nLoners are mostly unhappy. \n\nThat said, picking the right partner can determine 90% of your personal happiness or lack thereof.", sourceName: "The Blue Zones of Happiness: Lessons from the World’s Happiest People", sourceURL: "https://www.bluezones.com/blue-zones-of-happiness/", sponsorLogo: "", sponsorURL: "")

