let relationshipDay5 = Tip(header: "👰", title: "People who marry when they're very young are more likely to divorce", body: "An American who weds at 25 is 11% less likely to divorce than one who marries at age 24. \n\nBut waiting too long has a downside. Past the age of about 32-even after controlling for religion, education, geographic location, and other factors-the odds of divorce increase by 5% per year for at least the next decade.", sourceName: "When: The Scientific Secrets of Perfect Timing", sourceURL: "https://www.amazon.com/gp/product/B072Q985YX/ref=oh_aui_d_detailpage_o00_?ie=UTF8&psc=1", sponsorLogo: "", sponsorURL: "")
let relationshipDay6 = Tip(header: "🎓", title: "Finish as much education as you can before getting married", body: "Couples tend to be more satisfied with their marriages, and less likely to divorce, if they have more education before the wedding.", sourceName: "When: The Scientific Secrets of Perfect Timing", sourceURL: "https://www.amazon.com/gp/product/B072Q985YX/ref=oh_aui_d_detailpage_o00_?ie=UTF8&psc=1", sponsorLogo: "", sponsorURL: "")
let relationshipDay7 = Tip(header: "", title: "Happy Relationship Habit: A little PDA can go a long way", body: "The happiest couples aren’t afraid to show affection to each other – even in public. \n\nSo, go ahead and hold your partner's hand when you’re shopping in the mall or snuggle up to them when you’re at the movies with your friends. \n\n― Aaron Anderson, marriage and family therapist" , sourceName: "Huffington Post", sourceURL: "https://www.huffpost.com/entry/habits-of-people-in-happy-relationships_n_58586201e4b08debb78ac652", sponsorLogo: "", sponsorURL: "")

