let parentingPMTip0 = Tip(header: "🍽", title: "Teens who eat with their families 5+ times a week are more likely to get better grades in school and less likely to have substance abuse problems", body: "Survey: Teens who had family dinners 5+ times a week were: \n\n• 40% more likely to get A's and B's \n• 42% less likely to drink alcohol \n• 59% less likely to smoke cigarettes \n• 66% less likely to try marijuana \n\nAt a time when kids are under a lot of stress, having regular mealtimes are a great time for parents to listen and show support for their kids." , sourceName: "ABC News", sourceURL: "https://abcnews.go.com/WNT/Health/story?id=1123055&page=1", sponsorLogo: "", sponsorURL: "")
let parentingPMTip1 = Tip(header: "🗣", title: "For young children, dinnertime conversation increases vocabulary more than being read aloud", body: "Researchers counted the number of rare words – those not found on a list of 3,000 most common words – that the families used during dinner conversation. \n\nYoung kids learned 1,000 rare words at the dinner table, compared to only 143 from parents reading storybooks aloud. \n\nKids who have a large vocabulary read earlier and more easily." , sourceName: "The Washington Post", sourceURL: "https://www.washingtonpost.com/posteverything/wp/2015/01/12/the-most-important-thing-you-can-do-with-your-kids-eat-dinner-with-them/?utm_term=.21a61555e2d4", sponsorLogo: "", sponsorURL: "")
let parentingPMTip2 = Tip(header: "📺", title: "Study: American kindergartners who watched TV during dinner were more likely to be overweight by the time they were in 3rd grade", body: "In addition, a study of Northern European kids found that children were significantly more likely to be overweight the more often they viewed TV during dinner." , sourceName: "The Washington Post", sourceURL: "https://www.washingtonpost.com/posteverything/wp/2015/01/12/the-most-important-thing-you-can-do-with-your-kids-eat-dinner-with-them/?utm_term=.21a61555e2d4", sponsorLogo: "", sponsorURL: "")
let parentingPMTip3 = Tip(header: "🍟", title: "People living in communities with higher prevalence of fast-food restaurants were significantly less able to enjoy pleasurable activities that require savoring", body: "Fast food can incite people to feel more impatient, diminishing their ability to slow down and savor life's simpler joys. \n\nThe study's author suggests that if you want to raise kids where they're less impatient and able to delay gratification, then you should choose to live in a neighborhood where there is a lower concentration of fast-food restaurants." , sourceName: "Science Daily", sourceURL: "https://www.sciencedaily.com/releases/2014/06/140602155905.htm", sponsorLogo: "", sponsorURL: "")

