let parentingPMTip4 = Tip(header: "", title: "Sitting down to a family meal more often and cutting down on TV watching to 2 or fewer hours a day can help keep children from becoming overweight", body: "Study: Children who eat meals with their families eat more healthy foods than children who don't eat as many meals with their families. \n\nKids who watched the most TV (more than 2 hours a day) were at the greatest risk of being or becoming overweight." , sourceName: "Science Daily", sourceURL: "https://www.sciencedaily.com/releases/2007/01/070131112409.htm", sponsorLogo: "", sponsorURL: "")
let parentingPMTip5 = Tip(header: "", title: "Study: Eating together as a family during a child's adolescence resulted in adults who ate more fruit, dark-green and orange vegetables and key nutrients, and drank less soft drinks", body: "For both sexes, frequency of family meals as adolescents predicted improved diet quality during adulthood, eating dinner more frequently as adults, placing a higher priority on structured meals, and a higher priority on social eating." , sourceName: "Science Daily", sourceURL: "https://www.sciencedaily.com/releases/2007/01/070131112409.htm", sponsorLogo: "", sponsorURL: "")
let parentingPMTip6 = Tip(header: "", title: "Making it clear you have ambitions for your child's future and being a nagging parent helps to increase your child's success", body: "Study: Parents who consistently displayed high expectations for their children were: \n\n• More likely to attend college. \n• Less likely to become pregnant as teenagers. \n• Less likely to have prolonged periods of unemployment. \n• Less likely to get stuck in dead-end, low-wage jobs. \n\nNagging your child about finishing up their homework may not thrill them at the time, but they’ll thank you later in life." , sourceName: "Inc.", sourceURL: "https://www.inc.com/bill-murphy-jr/want-to-raise-successful-kids-science-says-do-these-5-things-every-day.html", sponsorLogo: "", sponsorURL: "")
let parentingPMTip7 = Tip(header: "", title: "Study: Children who eat fish at least once a week sleep better and have IQ scores that are 4 points higher, on average, than those who consume fish less frequently or not at all", body: "Children should be introduced to it early on. \n\n Consumption even once a week moves a family into the \"high\" fish-eating group as defined in the study." , sourceName: "Science Daily", sourceURL: "https://www.sciencedaily.com/releases/2017/12/171221101341.htm", sponsorLogo: "", sponsorURL: "")



//3
let parentingPMTip8 = Tip(header: "🍓", title: "Children who eat regular family dinners also consume more fruits, vegetables, vitamins and micronutrients, as well as fewer fried foods and soft drinks", body: "The nutritional benefits keep paying dividends even after kids grow up: young adults who ate regular family meals as teens are less likely to be obese and more likely to eat healthily once they live on their own." , sourceName: "The Washington Post", sourceURL: "https://www.washingtonpost.com/posteverything/wp/2015/01/12/the-most-important-thing-you-can-do-with-your-kids-eat-dinner-with-them/", sponsorLogo: "", sponsorURL: "")

let parentingPMTip9 = Tip(header: "", title: "Increase your kid's chances of success: Praise them for their effort instead of their natural abilities. It helps them develop a growth mindset instead of a fixed mindset", body: "• Natural ability praise (bad): Great job! You're so smart! \n• Effort praise (good): Great job! You worked hard and figured it out! \n\nIf you praise them for their natural intelligence, you're praising them for a) something they had nothing to do with achieving, and b) something they can't do anything to improve. But when you praise them for their effort, you're encouraging them to develop grit and a growth mindset to be successful in life. \n\nThe effects of these praise strategies are quantifiable even children are as young as 1 to 3 years of age." , sourceName: "Inc.", sourceURL: "https://www.inc.com/bill-murphy-jr/want-to-raise-successful-kids-science-says-do-these-5-things-every-day.html", sponsorLogo: "", sponsorURL: "")
let parentingPMTip10 = Tip(header: "", title: "Avoid feeding your kids foods high in fat and sugar because it can lower their IQ", body: "Study: Children who eat a poor diet (high fat and sugar) have an IQ 5 points lower than those with a health-conscious diet (salad, rice, pasta, fish, fruit)." , sourceName: "The Institute for Ethics and Emerging Technologies", sourceURL: "https://ieet.org/index.php/IEET2/more/pellissier20120109", sponsorLogo: "", sponsorURL: "")
let parentingPMTip11 = Tip(header: "", title: "Spend quality time with your kids tonight. Maternal bonding is important for your child's IQ", body: "Study: 4 year-olds who don’t have a close relationship with their mother have a lower IQ." , sourceName: "The Institute for Ethics and Emerging Technologies", sourceURL: "https://ieet.org/index.php/IEET2/more/pellissier20120109", sponsorLogo: "", sponsorURL: "")

